class Carousel extends HTMLElement{static get observedAttributes(){return['current','loop']}get current(){return this.getAttribute('current')||0}set current(a){a=parseInt(a);if(a>=this.children.length){a=this.loop?0:this.children.length-1}if(a<0){a=this.loop?this.children.length-1:0}for(let i=0;i<this.children.length;i++){const b=this.children[i];if(b.classList.contains('is-active')){b.classList.remove('is-active')}if(i===a){b.classList.add('is-active')}}return this.setAttribute('current',a)}get loop(){return this.hasAttribute('loop')}set loop(a){if(!!a){this.setAttribute('loop','loop')}else{this.removeAttribute('loop')}}connectedCallback(){this.current=this.current;this.loop=this.loop;setInterval(()=>this.current++,1000);this.classList.add('has-loaded')}}customElements.define('mr-carousel',Carousel);